import React, {useState} from 'react'
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import {useHistory} from 'react-router-dom';
import { Card, CardTitle, CardText, Row, Col } from 'reactstrap';
const EnterChat = (props) => {
    
    const [name, setName] = useState("");

    const sendName = (e) =>{
       setName(e.target.value);
    }

    const send = () =>{
        props.history.push('/room', {name:name})
    }

    return (
        <div className="container">
        <h5 style={{marginTop:10}}>Public list of available rooms!</h5>
        <Row style={{marginTop:50}}>
      <Col sm="4">
        <Card body>
          <CardTitle>React.js</CardTitle>
          <CardText>Talk about your experience with react and make new connections!</CardText>
          <Button>Go somewhere</Button>
        </Card>
      </Col>
      <Col sm="4">
        <Card body>
          <CardTitle>Vue.js</CardTitle>
          <CardText>Talk about your experience with react and make new connections!</CardText>
          <Button>Go somewhere</Button>
        </Card>
      </Col>
      <Col sm="4">
        <Card body>
          <CardTitle>Angular.js</CardTitle>
          <CardText>Talk about your experience with react and make new connections!</CardText>
          <Button>Go somewhere</Button>
        </Card>
      </Col>
    </Row>
        </div>
    )
}

export default EnterChat;
