import React, {useEffect, useState} from 'react'
import { Table } from 'reactstrap';
import socketIO from 'socket.io-client';
import TableRoll from './TableRoll'
const Livevisitors = () => {
    const ENDPOINT = "http://127.0.0.1:3001"
    var socket = socketIO(ENDPOINT);
    const [roomAndUser, setRoomAndUser] = useState({
    });
    useEffect(()=>{
      socket.on('connect', ()=>{});
        setInterval(() => {
          socket.emit('getListOfRooms', {room1:'React', room2:'Vue', room3:'Angular'});
        }, 3000);
     
    },[])

    return (
    <div className="container" style={{marginTop:20}}>
        <h5>Current visitors</h5>
        <Table>
      <thead>
        <tr>
          <th>Room</th>
          <th>People in room</th>
        </tr>
      </thead>
      <tbody>
         <tr>
             <td>React</td>
             <td></td>
          </tr>
          <tr>
             <td>Vue</td>
             <td></td>
          </tr>
          <tr>
             <td>Angular</td>
             <td></td>
          </tr>
      </tbody>
    </Table>
    </div>
    )
}

export default Livevisitors;
