    <div className="App" style={{display:'flex', flexDirection:"column", alignItems:'center'}}>
        <Paper elevation={8} style={{height:600, width:1000, marginTop:50, display:'flex', flexDirection:'column', alignItems:'center', }}>

            <Paper evelation={15} style={{height:400, width:800, marginTop:50, marginBottom:20, backgroundColor:'#e5e5ea'}}>

            </Paper>
