            <TextField name="comment" style={{width:400, marginLeft:100}} onChange={sendComment} InputProps={{
              startAdornment:(
                <InputAdornment position="start">
                    <SendIcon />
              </InputAdornment>
              )
            }}></TextField>
            </div>
        </Paper>
    </div>
  );
}

export default App;
