import React, {useEffect, useState} from 'react';
import Paper from '@material-ui/core/Paper'
import socketClient from 'socket.io-client'
import Button from '@material-ui/core/Button'
import TextField from '@material-ui/core/TextField';
import SendIcon from '@material-ui/icons/Send'; 
import ChatBubbleOutlineOutlinedIcon from '@material-ui/icons/ChatBubbleOutlineOutlined';
import InputAdornment from '@material-ui/core/InputAdornment';
const ENDPOINT = "http://127.0.0.1:3001"
function App() {

  const [successConnect, setConnect] = useState("");
  const [message, setMessage] = useState("");
  const socket = socketClient(ENDPOINT);

  useEffect(()=>{
     socket.on("Greet", data =>{
       setConnect(data);
     })
  },[])

  const sendComment = (e) =>{
      setMessage(e.target.value);
  }

  const sendMessage = () =>{
    console.log(message);
    socket.emit("chatMsg", message);
    setMessage("");
  }

  socket.on("chat", data =>{
    console.log(data);
  })

  return (
    <div className="App" style={{display:'flex', flexDirection:"column", alignItems:'center'}}>
        <Paper elevation={8} style={{height:600, width:1000, marginTop:50, display:'flex', flexDirection:'column', alignItems:'center', }}>

            <Paper evelation={15} style={{height:400, width:800, marginTop:50, marginBottom:20, backgroundColor:'#e5e5ea'}}>

            </Paper>
           <div style={{width:'100%', display:'flex', flexDirection:'row', justifyContent:'flex-start'}}>
            <TextField name="comment" style={{width:400, marginLeft:100}}  onChange={sendComment} InputProps={{
              startAdornment:(
                <InputAdornment position="start">
                    <ChatBubbleOutlineOutlinedIcon />
              </InputAdornment>
              )
            }}></TextField>
            <Button color="primary" onClick={sendMessage}
            style={{marginLeft:10, width:100}}
            variant="contained"
            startIcon={<SendIcon />}
      >SEND</Button>
            </div>
        </Paper>
    </div>
  );
}

export default App;
