import React, {useEffect, useState} from 'react';
import Paper from '@material-ui/core/Paper'
import socketClient from 'socket.io-client'
import Button from '@material-ui/core/Button'
import TextField from '@material-ui/core/TextField';
import SendIcon from '@material-ui/icons/Send'; 
import ChatBubbleOutlineOutlinedIcon from '@material-ui/icons/ChatBubbleOutlineOutlined';
import InputAdornment from '@material-ui/core/InputAdornment';
import { Scrollbars } from 'react-custom-scrollbars';
import ChatBubble from './component/ChatBubble';
import GreetingBubble from './component/GreetingBubble';
const ENDPOINT = "http://127.0.0.1:3001"
function App() {
  const [successConnect, setConnect] = useState("");
  const [disconnect, setDisconnect] = useState("");
  const [chat, setChat] = useState("");
  const [userCount, setCount] = useState(0);
  const [messagePool, setPool] = useState([]);
  const [message, setMessage] = useState("");
  const socket = socketClient(ENDPOINT);


  useEffect(()=>{
     socket.on("Greet", data =>{
       setConnect(data);
       setTimeout(() => {
         setConnect("");
       }, 6000);
     })
     socket.on("chat", data =>{
      setChat(data);
      setPool(state =>[...state, data]);
    })
    socket.on("UserCount", data =>{
      setCount(data);
    })
    socket.on('myCustomDisconnect',(data)=>{
      setDisconnect(data);
      setTimeout(() => {
        setDisconnect("");
      }, 6000);
      socket.disconnect();
    })
  },[])

  const sendComment = (e) =>{
      setMessage(e.target.value);
  }

  const sendMessage = () =>{
    socket.emit("chatMsg", message);
    setMessage("");
  }

  return (
    <div className="App" style={{display:'flex', flexDirection:"column", alignItems:'center'}}>

    </div>
  );
}

export default App;
